import React, { useState, useEffect } from "react";
import DetailPresenter from "./DetailPresenter";
import { movieApi } from "../../api";
export default ({
  navigation,
  route: {
    params: { id, title, backgroundImage, poster, votes, overview },
  },
}) => {
  const [movie, setMovie] = useState({
    title,
    backgroundImage,
    poster,
    overview,
    votes,
  });
  const getData = async () => {
    console.log(id);
    const [getMovie, getMovieError] = await movieApi.movie(id);
    console.log(getMovie, getMovieError);
    setMovie({
      title: getMovie.title,
      backgroundImage: getMovie.poster_path,
      poster: getMovie.poster_path,
    });
  };
  useEffect(() => {
    getData();
  }, [id]);
  React.useLayoutEffect(() => {
    navigation.setOptions({ title });
  });
  return <DetailPresenter poster={poster} />;
};
