import React, { useState, useEffect } from "react";
import DetailPresenter from "./DetailPresenter";
import { movieApi, tvApi } from "../../api";
export default ({
  navigation,
  route: {
    params: { isTv, id, title, backgroundImage, poster, votes, overview },
  },
}) => {
  const [loading, setLoading] = useState(true);
  const [detail, setDetail] = useState({
    title,
    backgroundImage,
    poster,
    overview,
    votes,
  });
  let [getDetail, getDetailError] = [null, null];
  const getData = async () => {
    if (isTv) {
      [getDetail, getDetailError] = await tvApi.show(id);
    } else {
      [getDetail, getDetailError] = await movieApi.movie(id);
    }
    setDetail({
      ...getMovie,
      title: getMovie.title,
      backgroundImage: getMovie.backdrop_path,
      poster: getMovie.poster_path,
      overview: getMovie.overview,
      votes: getMovie.vote_average,
    });
    // setLoading(false);
  };
  useEffect(() => {
    getData();
  }, [id]);
  React.useLayoutEffect(() => {
    navigation.setOptions({ title });
  });
  return <DetailPresenter movie={detail} loading={loading} />;
};
