import React, { useState } from "react";
import SearchPresenter from "./SearchPresent";
import { movieApi, tvApi } from "../../api";

export default () => {
  const [keyword, setKeyword] = useState("");
  const [results, setResults] = useState({
    movies: [],
    shows: [],
    movieError: null,
    showsError: null,
  });
  const onChange = (text) => setKeyword(text);
  const search = () => {
    const [movies, moviesError] = movieApi.search(keyword);
    const [shows, showsError] = tvApi.search(keyword);
    setResults({ movies, shows, movieError, showsError });
  };
  console.log(results);

  return (
    <SearchPresenter onChange={onChange} onSubmit={search} keyword={keyword} />
  );
};
